namespace apiBozzi.Utils.Demo
{
    /// <summary>
    /// Define um gerador que pode criar dados de demonstração para um tipo específico.
    /// </summary>
    public interface IDemoDataGenerator
    {
        /// <summary>
        /// Verifica se este gerador pode lidar com o tipo de objeto fornecido.
        /// </summary>
        /// <param name="type">O tipo do objeto de dados.</param>
        /// <returns>True se o gerador puder lidar com o tipo; caso contrário, false.</returns>
        bool CanGenerate(Type type);

        /// <summary>
        /// Gera o objeto de demonstração a partir do objeto original.
        /// </summary>
        /// <param name="originalData">Os dados originais da action.</param>
        /// <returns>Um objeto com dados de demonstração.</returns>
        object Generate(object originalData);
    }
}

